mapper.select:
select * from post where module_name = #{module_name}
or module_name is null
order by post_createtime desc

select post.post_id,count(1) from post INNER JOIN post_like on post.post_id = post_like.post_id where post.module_name = #{module_name}
or module_name is null
and post_like.like_time >= DATE_ADD(UTC_TIMESTAMP,INTERVAL -1 DAY) GROUP BY (post.post_id) order by post.post_id asc

select post.post_id,count(1) from post INNER JOIN post_comment on post.post_id = post_comment.post_id where post.module_name = #{module_name}
or module_name is null
and post_comment.comment_time >= DATE_ADD(UTC_TIMESTAMP,INTERVAL -1 DAY) GROUP BY (post.post_id) order by post.post_id asc
